import { useContext } from "react";
import { Link } from "react-router-dom";

function Navigation() {
    const {globalCount, setGlobalCount} = useContext(global
    );
    return (
        <div style={{display:'flex', flexDirection:'row', justifyContent:'space-between', backgroundColor:"black", padding:'20px'}}>
            <div style={{display:'flex', gap:'10px', cursor:'pointer', fontSize:20}}>
                <Link to="/" style={{textDecoration:'none', color:'white'}}>Home</Link>
                <Link to="/reviews" style={{textDecoration:'none', color:'white'}}>Products</Link>
            </div>
            <div style={{display:'flex', gap:'10px', cursor:'pointer', fontSize:20}}>
                
                <Link to="/cart" style={{textDecoration:'none', color:'white'}}>cart({globalCount})</Link>
                <Link to="/profile" style={{textDecoration:'none', color:'white'}}>profile</Link>
            </div>
        </div>
    )
}
export default Navigation;
