import { useContext } from "react";
import { globalContext } from "./App";
import Price from "./price";
import Navigation from "./navigation";

function AddToCart() {
    const {card} = useContext(globalContext);
    const {globalCount} = useContext(globalContext);
    return (<div>
        <Navigation/>
        <div style={{margin:'auto'}}>
        <h1 style={{margin:20}}>Cart</h1>
        </div>
        <div style={{display:'flex'}}>
            <div>
                
                {card.map((item, index) => (
                    <div key={index} style={{width:700, height:150, backgroundColor:'white', boxShadow:'0px 4px 8px rgba(0,0,0,0.9)', margin:20, borderRadius:10, padding:50, paddingTop:20, paddingBottom:20, display:'flex'}}>
                        <img src={item.path} alt="pic" width={130} height={150} />
                        <div style={{marginLeft:20}}>
                            <h3>{item.title}</h3>
                                <Price p={item.price} q={item.quantity}/>
                        </div>
                    </div>
                ))}
            </div>
            <div style={{width:700, height:150, backgroundColor:'white', boxShadow:'0px 4px 8px rgba(0,0,0,0.15)', margin:20, borderRadius:10, padding:50, paddingTop:20, paddingBottom:20}}>
                <h3>Price Details</h3>
                <div>
                    <p>Price ({globalCount}) items</p>
                    <p>{card.price}</p>
                </div>
            </div>
        </div>
    </div>);
}
export default AddToCart;
