import { BrowserRouter, Route, Routes } from "react-router-dom";
import Datafetch from "./datafetching";
import Home from "./home";
import ProductsReviews from "./productReviews";
import { createContext, useState } from "react";
import Profile from "./profile";

export const globalContext = createContext();
function App() {
    const [globalCount, setGlobalCount] = useState(2);
    return (
        <globalContext.Provider value = {{globalCount, setGlobalCount}}>
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Home/>}/>
                    <Route path="/products" element={<Datafetch/>}/>
                    <Route path="/cart" element={<ProductsReviews/>}/>
                    <Route path="/profile" element={<Profile/>}/>
                </Routes>
            </BrowserRouter>
        </globalContext.Provider>
    );
}
export default App;
