import React, { useContext, useState } from 'react';
import { globalContext } from './App';

function Price({ card }) {
    const { setGlobalCount } = useContext(globalContext);
    const [number, setNumber] = useState(1);
    const [number1, setNumber1] = useState(card.price);

    const funcBtnClick = () => {
        if (number > 1) {
            setNumber(number - 1);
            setNumber1(card.price * (number - 1));
            setGlobalCount((prevCount) => prevCount - 1);
        }
    };

    const funcBtnClick1 = () => {
        setNumber(number + 1);
        setNumber1(card.price * (number + 1));
        setGlobalCount((prevCount) => prevCount + 1);
    };

    return (
        <div>
            <h4>Price: Rs. {card.price}</h4>
            <h4>Total: Rs. {number1}</h4>
            <div style={{ marginTop: -23 }}>
                <div style={{ display: 'flex' }}>
                    <button
                        className="btn btn-primary"
                        onClick={funcBtnClick}
                        style={{ margin: 15, padding: 5 }}
                    >
                        <strong>-</strong>
                    </button>
                    <h3>{number}</h3>
                    <button
                        className="btn btn-primary"
                        onClick={funcBtnClick1}
                        style={{ margin: 15, padding: 5 }}
                    >
                        <strong>+</strong>
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Price;
