import React, { useContext } from "react";
import { globalContext } from "./App";

class Price extends React.Component {
    name = "Rs.";
    count = 0;

    constructor(props) {
        super(props);
        this.state = { mrp: 70000, number: 1, number1: 70000 };
        console.log("Constructor is called");
    }

    componentDidMount() {
        console.log("Mounted");
        console.log(this.count);
        console.log(this.count + 1);
        console.log(this.state.number + 1);
        console.log(this.state.mrp * this.state.number);
    }

    componentWillUnmount() {
        console.log("Unmounted");
    }

    componentDidUpdate() {
        console.log("Updated!!!");
    }

    funcBtnClick = () => {
        console.log("Button Clicked");
        console.log(this.count - 1);
        if(this.state.number > 1) {
        this.setState((prevState) => ({
            number: prevState.number - 1,
            number1: prevState.mrp * (prevState.number - 1),
        }));
    }
        this.context.setGlobalCount(this.context.globalCount - 1);
    };

    funcBtnClick1 = () => {
        console.log("Button Clicked");
        console.log(this.count + 1);
        this.setState((prevState) => ({
            number: prevState.number + 1,
            number1: prevState.mrp * (prevState.number + 1),
        }));
        this.context.setGlobalCount(this.context.globalCount + 1);
    };

    render() {
        return (
            <div>
                <h4>Price {this.state.mrp}</h4>
                <h4>
                    {this.name} {this.state.number1}
                </h4>
                <div style={{marginTop:-15}}>
                    {/* {this.state.number === 0 ? (
                        <div>
                            <button
                                className="btn btn-primary"
                                onClick={this.funcBtnClick1}
                                style={{ margin: 15, padding: 5 }}
                            >
                                Add to cart
                            </button>
                        </div>
                    ) : ( */}
                        <div style={{ display: "flex" }}>
                            <button
                                className="btn btn-primary"
                                onClick={this.funcBtnClick}
                                style={{ margin: 15, padding: 5 }}
                            >
                                <strong>-</strong>
                            </button>
                            <h3>{this.state.number}</h3>
                            <button
                                className="btn btn-primary"
                                onClick={this.funcBtnClick1}
                                style={{ margin: 15, padding: 5 }}
                            >
                                <strong>+</strong>
                            </button>
                        </div>
                    {/* )} */}
                </div>
            </div>
        );
    }
}

Price.contextType = globalContext;

export default Price;
