function Products({imgPath}) {
    const func = () => {
        const obj = {
            id, path, title, price, descript, category, rating, count, quantity: 1
        };
        setCard((prevCards) => {
            const existingProduct = prevCards.find((product) => product.id === obj.id);
            if (existingProduct) {
                const updatedCards = prevCards.map((product) =>
                    product.id === obj.id
                        ? { ...product, quantity: product.quantity + 1 }
                        : product
                );
                setGlobalPrice(globalPrice + obj.price); // Update total price
                return updatedCards;
            } else {
                setGlobalPrice(globalPrice + obj.price); // Update total price
                return [...prevCards, obj];
            }
        });

        setGlobalCount(globalCount + 1);
    };
    return (
        <div style={{display:'flex', margin:40}}>
            <img src = {imgPath}></img>
            <div style={{margin:30, marginTop:5}}>
                <h2>iPhone</h2>
                <p>Original Smartphone Compatible with Apple i-Phone 6 Gold (64 GB Storage with 1-Year Warranty)</p>
                <br/>
                <center>
                <button onClick={func} style={{ backgroundColor: 'white', border: "0.2px solid black", width: 200, height: 30 }}>
                    <div style={{ display: 'flex', alignItems: 'center', marginLeft: 40 }}>
                        <img src="cartSymbol.jpeg" alt="cart" width={30} height={25}></img>Add to cart
                    </div>
                </button>
            </center>
            </div>
            
        </div>
    )
}
export default Products;
