import { useContext } from "react";
import Navigation from "./navigation";
import Products from "./products";
import Reviews from "./reviews";
import { globalContext } from "./App";

function ProductsReviews() {
    const {card, setCard} = useContext(globalContext);
    return (
        <div style={{margin:20, padding:5}}>
            <Navigation/>
            <div style={{ backgroundColor:'white', boxShadow:'0px 4px 8px rgba(0,0,0,0.9)', margin:20, borderRadius:10, padding:50, paddingTop:20, paddingBottom:20, display:'flex'}}>
                        <img src={card.path} alt="pic" width={130} height={150} />
                        <div style={{marginLeft:20}}>
                            <h3>{card.title}</h3>
                                <Price p={card.price} q={card.quantity}/>
                        </div>
                    </div><br/>
            <div style={{border:"1px solid black"}}> <Reviews/> </div>
        </div>
    )
}
export default ProductsReviews;
